SELECT d.nombre AS departamento, p.apellido1, p.apellido2, p.nombre FROM persona p LEFT JOIN profesor pr ON p.id = pr.id_profesor LEFT JOIN departamento d ON pr.id_departamento = d.id WHERE tipo='profesor' ORDER BY d.nombre ASC, p.apellido1 ASC, p.apellido2 ASC, p.nombre ASC; -- Retorna un llistat amb els noms de tots els professors i els departaments que tenen vinculats. El llistat també ha de mostrar aquells professors que no tenen cap departament associat. El llistat ha de retornar quatre columnes, nom del departament, primer cognom, segon cognom i nom del professor. El resultat estarà ordenat alfabèticament de menor a major pel nom del departament, cognoms i el nom.
SELECT p.apellido1, p.apellido2, p.nombre FROM persona p LEFT JOIN profesor pr ON p.id = pr.id_profesor LEFT JOIN departamento d ON pr.id_departamento = d.id WHERE p.tipo='profesor'  AND d.id IS NULL; -- Retorna un llistat amb els professors que no estan associats a un departament.
SELECT * FROM departamento d LEFT JOIN  profesor pr ON d.id = pr.id_departamento WHERE pr.id_departamento IS NULL; -- Retorna un llistat amb els departaments que no tenen professors associats.
SELECT p.apellido1, p.apellido2, p.nombre FROM persona p LEFT JOIN profesor pr ON p.id = pr.id_profesor LEFT JOIN asignatura a ON pr.id_profesor = a.id_profesor WHERE p.tipo = 'profesor' AND a.nombre IS NULL; -- Retorna un llistat amb els professors que no imparteixen cap assignatura.
SELECT a.id, a.nombre FROM asignatura a LEFT JOIN profesor pr ON a.id_profesor = pr.id_profesor WHERE pr.id_profesor IS NULL; -- Retorna un llistat amb les assignatures que no tenen un professor assignat.
SELECT DISTINCT d.nombre as departamentos_sin_imparticiones FROM departamento d LEFT JOIN profesor pr ON d.id = pr.id_departamento LEFT JOIN asignatura a ON pr.id_profesor = a.id_profesor LEFT JOIN alumno_se_matricula_asignatura m ON m.id_asignatura = a.id LEFT JOIN curso_escolar c ON  c.id = m.id_curso_escolar WHERE c.anyo_inicio IS NULL; -- Retorna un llistat amb tots els departaments que no han impartit assignatures en cap curs escolar.